graphql.query:
mutation Login($loginUsername: String!, $loginPassword: String!) {
  login(username: $loginUsername, password: $loginPassword) {
    token
    refreshToken
  }
}

mutation ChangePassword(
  $changePasswordOldPassword: String!
  $changePasswordNewPassword: String!
) {
  changePassword(
    oldPassword: $changePasswordOldPassword
    newPassword: $changePasswordNewPassword
  ) {
    ok
  }
}
